import { useState } from "react";
import { motion } from "framer-motion";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Play, 
  Square, 
  BarChart3, 
  MoreVertical,
  Server,
  Database,
  Zap,
  HardDrive
} from "lucide-react";
import { cn } from "@/lib/utils";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

interface AWSService {
  id: string;
  name: string;
  type: 'EC2' | 'S3' | 'RDS' | 'Lambda';
  status: 'running' | 'stopped' | 'pending';
  region: string;
  cost: number;
  usage: number;
  icon: React.ElementType;
}

interface AWSServiceCardProps {
  service: AWSService;
  onAction: (serviceId: string, action: string) => void;
  shouldReduceMotion?: boolean;
}

const statusColors = {
  running: "bg-chart-2/20 text-chart-2 border-chart-2/30",
  stopped: "bg-destructive/20 text-destructive border-destructive/30", 
  pending: "bg-chart-3/20 text-chart-3 border-chart-3/30"
};

const serviceTypeIcons = {
  EC2: Server,
  S3: HardDrive,
  RDS: Database,
  Lambda: Zap
};

export default function AWSServiceCard({ service, onAction, shouldReduceMotion = false }: AWSServiceCardProps) {
  const [isLoading, setIsLoading] = useState(false);
  const StatusIcon = service.status === 'running' ? Square : Play;
  const ServiceIcon = serviceTypeIcons[service.type];

  const handleAction = async (action: string) => {
    setIsLoading(true);
    console.log(`${action} action on ${service.name}`);

    // Simulate API call
    setTimeout(() => {
      onAction(service.id, action);
      setIsLoading(false);
    }, 1000);
  };

  return (
    <motion.div
      initial={shouldReduceMotion ? false : { opacity: 0, y: 20 }}
      animate={shouldReduceMotion ? false : { opacity: 1, y: 0 }}
      whileHover={shouldReduceMotion ? undefined : { y: -2 }}
      transition={shouldReduceMotion ? undefined : { duration: 0.2 }}
    >
      <Card className="bg-card/80 backdrop-blur-sm border-white/20 hover-elevate group">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <div className="flex items-center space-x-3">
            <div className="w-10 h-10 bg-primary/20 rounded-lg flex items-center justify-center">
              <ServiceIcon className="w-5 h-5 text-primary" />
            </div>
            <div>
              <CardTitle className="text-sm font-semibold text-foreground">{service.name}</CardTitle>
              <p className="text-xs text-muted-foreground">{service.type} • {service.region}</p>
            </div>
          </div>

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button 
                variant="ghost" 
                size="icon" 
                className="w-8 h-8"
                data-testid={`menu-${service.id}`}
              >
                <MoreVertical className="w-4 h-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent className="bg-popover/95 backdrop-blur-xl border-white/20">
              <DropdownMenuItem onClick={() => handleAction('monitor')}>
                <BarChart3 className="mr-2 w-4 h-4" />
                Monitor
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => handleAction('configure')}>
                Configure
              </DropdownMenuItem>
              <DropdownMenuItem onClick={() => handleAction('terminate')} className="text-destructive">
                Terminate
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </CardHeader>

        <CardContent className="space-y-4">
          <div className="flex items-center justify-between">
            <Badge className={cn("text-xs border", statusColors[service.status])}>
              {service.status.charAt(0).toUpperCase() + service.status.slice(1)}
            </Badge>
            <span className="text-sm font-medium text-foreground">
              ${service.cost.toFixed(2)}/month
            </span>
          </div>

          {/* Usage Bar */}
          <div className="space-y-2">
            <div className="flex justify-between text-xs">
              <span className="text-muted-foreground">Usage</span>
              <span className="text-foreground">{service.usage}%</span>
            </div>
            <div className="w-full bg-background/50 rounded-full h-2 overflow-hidden">
              <motion.div
                initial={shouldReduceMotion ? false : { width: 0 }}
                animate={shouldReduceMotion ? false : { width: `${service.usage}%` }}
                transition={shouldReduceMotion ? undefined : { duration: 1, delay: 0.2 }}
                className={cn(
                  "h-full rounded-full",
                  service.usage > 80 ? "bg-destructive" :
                  service.usage > 60 ? "bg-chart-3" : "bg-primary"
                )}
              />
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex space-x-2">
            <Button
              size="sm"
              variant={service.status === 'running' ? 'destructive' : 'default'}
              className="flex-1"
              onClick={() => handleAction(service.status === 'running' ? 'stop' : 'start')}
              disabled={isLoading || service.status === 'pending'}
              data-testid={`button-toggle-${service.id}`}
            >
              {isLoading ? (
                <motion.div
                  animate={shouldReduceMotion ? false : { rotate: 360 }}
                  transition={shouldReduceMotion ? undefined : { duration: 1, repeat: Infinity, ease: "linear" }}
                  className="w-4 h-4 border-2 border-current border-t-transparent rounded-full"
                />
              ) : (
                <>
                  <StatusIcon className="w-4 h-4 mr-2" />
                  {service.status === 'running' ? 'Stop' : 'Start'}
                </>
              )}
            </Button>

            <Button
              size="sm"
              variant="outline"
              onClick={() => handleAction('monitor')}
              className="bg-background/50 border-white/20 hover:bg-background/70"
              data-testid={`button-monitor-${service.id}`}
            >
              <BarChart3 className="w-4 h-4" />
            </Button>
          </div>
        </CardContent>
      </Card>
    </motion.div>
  );
}

client/src/components/ActivityFeed.tsx.content:
import { motion, useReducedMotion } from "framer-motion";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { 
  Activity, 
  Server, 
  Database, 
  DollarSign, 
  Users, 
  Shield,
  CheckCircle,
  AlertTriangle,
  Info,
  Clock
} from "lucide-react";
import { containerVariants, cardVariants } from "@/utils/motionVariants";
import { cn } from "@/lib/utils";

interface ActivityItem {
  id: string;
  type: 'success' | 'warning' | 'info' | 'error';
  title: string;
  description: string;
  timestamp: string;
  user?: string;
  service?: string;
  icon: React.ElementType;
}

interface ActivityFeedProps {
  onViewAll: () => void;
  onItemClick: (id: string) => void;
}

// TODO: remove mock data  
const activities: ActivityItem[] = [
  {
    id: '1',
    type: 'success',
    title: 'EC2 instance launched successfully',
    description: 'web-server-03 is now running in us-east-1',
    timestamp: '2 minutes ago',
    user: 'Sarah Chen',
    service: 'EC2',
    icon: Server
  },
  {
    id: '2', 
    type: 'info',
    title: 'Auto-scaling group updated',
    description: 'Minimum instances increased from 2 to 3',
    timestamp: '15 minutes ago',
    user: 'System',
    service: 'Auto Scaling',
    icon: Activity
  },
  {
    id: '3',
    type: 'warning',
    title: 'High memory usage detected',
    description: 'Database server using 85% memory',
    timestamp: '1 hour ago',
    service: 'CloudWatch',
    icon: AlertTriangle
  },
  {
    id: '4',
    type: 'success',
    title: 'Backup completed',
    description: 'prod-database backup finished successfully',
    timestamp: '2 hours ago',
    user: 'Automated',
    service: 'RDS',
    icon: Database
  },
  {
    id: '5',
    type: 'info',
    title: 'New IAM user created',
    description: 'developer-access role assigned to john.doe',
    timestamp: '3 hours ago',
    user: 'Sarah Chen',
    service: 'IAM',
    icon: Users
  },
  {
    id: '6',
    type: 'success',
    title: 'Cost optimization applied',
    description: 'Saved $234 by right-sizing instances',
    timestamp: '4 hours ago',
    user: 'Cost Optimizer',
    service: 'Cost Explorer',
    icon: DollarSign
  }
];

const getActivityIcon = (type: string) => {
  switch (type) {
    case 'success': return CheckCircle;
    case 'warning': return AlertTriangle;
    case 'error': return AlertTriangle;
    default: return Info;
  }
};

const getActivityColor = (type: string) => {
  switch (type) {
    case 'success': return 'text-chart-2 bg-chart-2/10';
    case 'warning': return 'text-chart-3 bg-chart-3/10';
    case 'error': return 'text-destructive bg-destructive/10';
    default: return 'text-primary bg-primary/10';
  }
};

export default function ActivityFeed({ onViewAll, onItemClick }: ActivityFeedProps) {
  const shouldReduceMotion = useReducedMotion();

  return (
    <motion.div
      variants={shouldReduceMotion ? undefined : containerVariants}
      initial={shouldReduceMotion ? false : "hidden"}
      animate={shouldReduceMotion ? false : "visible"}
    >
      <Card className="gradient-border noise-overlay bg-card/80 backdrop-blur-sm border-white/10">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
          <CardTitle className="text-lg font-semibold text-foreground flex items-center">
            <Activity className="w-5 h-5 mr-2 text-primary" />
            Recent Activity
          </CardTitle>
          <Button
            variant="outline"
            size="sm"
            onClick={onViewAll}
            className="bg-background/50 border-white/20 hover:bg-background/70"
            data-testid="view-all-activity"
          >
            View all
          </Button>
        </CardHeader>

        <CardContent className="space-y-3">
          {activities.map((activity, index) => {
            const ActivityIcon = getActivityIcon(activity.type);
            const ServiceIcon = activity.icon;

            return (
              <motion.div
                key={activity.id}
                variants={shouldReduceMotion ? undefined : cardVariants}
                whileHover={shouldReduceMotion ? undefined : "hover"}
                className="organic-hover cursor-pointer"
                onClick={() => onItemClick(activity.id)}
                data-testid={`activity-${activity.id}`}
              >
                <div className="flex items-start space-x-3 p-3 rounded-lg bg-background/30 border border-white/10 hover:bg-background/50 transition-colors">
                  {/* Status Icon */}
                  <div className={cn(
                    "w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0",
                    getActivityColor(activity.type)
                  )}>
                    <ActivityIcon className="w-4 h-4" />
                  </div>

                  {/* Content */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-start justify-between">
                      <div className="space-y-1">
                        <p className="text-sm font-medium text-foreground">
                          {activity.title}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {activity.description}
                        </p>

                        {/* Meta Info */}
                        <div className="flex items-center space-x-2 text-xs text-muted-foreground">
                          <Clock className="w-3 h-3" />
                          <span>{activity.timestamp}</span>
                          {activity.service && (
                            <>
                              <span>•</span>
                              <span>{activity.service}</span>
                            </>
                          )}
                        </div>
                      </div>

                      {/* Service Icon */}
                      <div className="w-6 h-6 bg-primary/20 rounded flex items-center justify-center flex-shrink-0">
                        <ServiceIcon className="w-3 h-3 text-primary" />
                      </div>
                    </div>

                    {/* User Info */}
                    {activity.user && (
                      <div className="flex items-center space-x-2 mt-2">
                        <Avatar className="w-5 h-5">
                          <AvatarFallback className="bg-primary/20 text-primary text-xs">
                            {activity.user.split(' ').map(n => n[0]).join('')}
                          </AvatarFallback>
                        </Avatar>
                        <span className="text-xs text-muted-foreground">
                          by {activity.user}
                        </span>
                      </div>
                    )}
                  </div>
                </div>
              </motion.div>
            );
          })}

          {/* Empty state would go here for when there are no activities */}
          {activities.length === 0 && (
            <div className="text-center py-8">
              <Activity className="w-12 h-12 mx-auto text-muted-foreground/50 mb-3" />
              <p className="text-sm text-muted-foreground">No recent activity</p>
              <p className="text-xs text-muted-foreground mt-1">
                Activity will appear here as you use AWS services
              </p>
            </div>
          )}
        </CardContent>
      </Card>
    </motion.div>
  );
}

client/src/components/BillingChart.tsx.content:
import { motion, useReducedMotion } from "framer-motion";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { TrendingUp, TrendingDown, DollarSign, Calendar, Download } from "lucide-react";
import { LineChart, Line, XAxis, YAxis, ResponsiveContainer, AreaChart, Area, Tooltip } from "recharts";

// TODO: remove mock data
const mockData = [
  { month: 'Jan', cost: 120, services: 8 },
  { month: 'Feb', cost: 135, services: 9 },
  { month: 'Mar', cost: 98, services: 7 },
  { month: 'Apr', cost: 156, services: 11 },
  { month: 'May', cost: 189, services: 12 },
  { month: 'Jun', cost: 167, services: 10 },
];

const serviceBreakdown = [
  { name: 'EC2', cost: 89.45, percentage: 53.6, color: 'hsl(var(--chart-1))' },
  { name: 'S3', cost: 23.12, percentage: 13.8, color: 'hsl(var(--chart-2))' },
  { name: 'RDS', cost: 34.67, percentage: 20.8, color: 'hsl(var(--chart-3))' },
  { name: 'Lambda', cost: 19.76, percentage: 11.8, color: 'hsl(var(--chart-4))' },
];

interface BillingChartProps {
  onDownloadReport: () => void;
  onViewDetails: () => void;
}

export default function BillingChart({ onDownloadReport, onViewDetails }: BillingChartProps) {
  const currentMonthCost = 167;
  const lastMonthCost = 189;
  const costChange = ((currentMonthCost - lastMonthCost) / lastMonthCost) * 100;
  const isDecreasing = costChange < 0;
  const shouldReduceMotion = useReducedMotion();

  return (
    <motion.div
      initial={shouldReduceMotion ? false : { opacity: 0, y: 20 }}
      animate={shouldReduceMotion ? false : { opacity: 1, y: 0 }}
      transition={shouldReduceMotion ? undefined : { delay: 0.2 }}
    >
      <Card className="bg-card/80 backdrop-blur-sm border-white/20">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
          <div className="space-y-1">
            <CardTitle className="text-lg font-semibold text-foreground flex items-center">
              <DollarSign className="w-5 h-5 mr-2 text-primary" />
              Cost & Billing
            </CardTitle>
            <p className="text-sm text-muted-foreground">Your AWS spending overview</p>
          </div>

          <div className="flex space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={onDownloadReport}
              className="bg-background/50 border-white/20 hover:bg-background/70"
              data-testid="button-download-report"
            >
              <Download className="w-4 h-4 mr-2" />
              Export
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={onViewDetails}
              className="bg-background/50 border-white/20 hover:bg-background/70"
              data-testid="button-view-details"
            >
              <Calendar className="w-4 h-4 mr-2" />
              Details
            </Button>
          </div>
        </CardHeader>

        <CardContent className="space-y-6">
          {/* Cost Summary */}
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <p className="text-sm text-muted-foreground">This Month</p>
              <div className="flex items-center space-x-2">
                <span className="text-2xl font-bold text-foreground">${currentMonthCost}</span>
                <Badge 
                  className={`${isDecreasing ? 'bg-chart-2/20 text-chart-2' : 'bg-destructive/20 text-destructive'} border-0`}
                >
                  {isDecreasing ? <TrendingDown className="w-3 h-3 mr-1" /> : <TrendingUp className="w-3 h-3 mr-1" />}
                  {Math.abs(costChange).toFixed(1)}%
                </Badge>
              </div>
            </div>

            <div className="space-y-2">
              <p className="text-sm text-muted-foreground">Projected</p>
              <span className="text-2xl font-bold text-primary">${(currentMonthCost * 1.1).toFixed(0)}</span>
            </div>
          </div>

          {/* Cost Trend Chart */}
          <div className="h-48">
            <ResponsiveContainer width="100%" height="100%">
              <AreaChart data={mockData}>
                <defs>
                  <linearGradient id="costGradient" x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor="hsl(var(--primary))" stopOpacity={0.3}/>
                    <stop offset="95%" stopColor="hsl(var(--primary))" stopOpacity={0}/>
                  </linearGradient>
                </defs>
                <XAxis 
                  dataKey="month" 
                  axisLine={false}
                  tickLine={false}
                  tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }}
                />
                <YAxis hide />
                <Tooltip 
                  contentStyle={{
                    backgroundColor: 'hsl(var(--popover))',
                    border: '1px solid rgba(255,255,255,0.1)',
                    borderRadius: '8px',
                    backdropFilter: 'blur(12px)'
                  }}
                />
                <Area
                  type="monotone"
                  dataKey="cost"
                  stroke="hsl(var(--primary))"
                  strokeWidth={2}
                  fill="url(#costGradient)"
                />
              </AreaChart>
            </ResponsiveContainer>
          </div>

          {/* Service Breakdown */}
          <div className="space-y-3">
            <h4 className="text-sm font-medium text-foreground">Service Breakdown</h4>
            <div className="space-y-2">
              {serviceBreakdown.map((service, index) => (
                <motion.div
                  key={service.name}
                  initial={shouldReduceMotion ? false : { opacity: 0, x: -20 }}
                  animate={shouldReduceMotion ? false : { opacity: 1, x: 0 }}
                  transition={shouldReduceMotion ? undefined : { delay: 0.1 * index }}
                  className="flex items-center justify-between"
                >
                  <div className="flex items-center space-x-3">
                    <div 
                      className="w-3 h-3 rounded-full"
                      style={{ backgroundColor: service.color }}
                    />
                    <span className="text-sm text-foreground">{service.name}</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className="text-sm text-muted-foreground">{service.percentage}%</span>
                    <span className="text-sm font-medium text-foreground">${service.cost}</span>
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        </CardContent>
      </Card>
    </motion.div>
  );
}

