import { useState } from "react";
import { motion } from "framer-motion";
import { Search, Bell, User, Menu, Moon, Sun } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { useTheme } from "@/components/ThemeProvider";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

interface GlassNavbarProps {
  onMenuToggle: () => void;
  onSearch: (query: string) => void;
  user: {
    name: string;
    email: string;
    avatar?: string;
  };
}

export default function GlassNavbar({ onMenuToggle, onSearch, user }: GlassNavbarProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const { theme, toggleTheme } = useTheme();

  const handleSearchSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSearch(searchQuery);
  };

  return (
    <motion.header
      initial={{ y: -50, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.3 }}
      className="h-16 bg-card/80 backdrop-blur-xl border-b border-white/10 flex items-center justify-between px-4 sticky top-0 z-50"
    >
      {/* Left Section */}
      <div className="flex items-center space-x-4">
        <Button
          variant="ghost"
          size="icon"
          className="lg:hidden"
          onClick={onMenuToggle}
          data-testid="button-menu-toggle"
        >
          <Menu className="w-5 h-5" />
        </Button>

        {/* Search */}
        <form onSubmit={handleSearchSubmit} className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <Input
            type="search"
            placeholder="Search resources, users, costs..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10 w-80 bg-background/50 border-white/20 focus:border-primary/50 backdrop-blur-sm"
            data-testid="input-search"
          />
        </form>
      </div>

      {/* Right Section */}
      <div className="flex items-center space-x-4">
        {/* Theme Toggle */}
        <Button
          variant="ghost"
          size="icon"
          onClick={toggleTheme}
          data-testid="button-theme-toggle"
        >
          {theme === 'dark' ? <Sun className="w-4 h-4" /> : <Moon className="w-4 h-4" />}
        </Button>

        {/* Notifications */}
        <div className="relative">
          <Button
            variant="ghost"
            size="icon"
            data-testid="button-notifications"
          >
            <Bell className="w-4 h-4" />
          </Button>
          <Badge className="absolute -top-1 -right-1 w-5 h-5 rounded-full bg-destructive text-destructive-foreground text-xs flex items-center justify-center p-0">
            3
          </Badge>
        </div>

        {/* User Menu */}
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button
              variant="ghost"
              className="relative h-8 w-8 rounded-full"
              data-testid="button-user-menu"
            >
              <Avatar className="h-8 w-8">
                <AvatarImage src={user.avatar} alt={user.name} />
                <AvatarFallback className="bg-primary/20 text-primary">
                  {user.name.split(' ').map(n => n[0]).join('').toUpperCase()}
                </AvatarFallback>
              </Avatar>
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent className="w-56 bg-popover/95 backdrop-blur-xl border-white/20" align="end">
            <div className="flex items-center space-x-2 p-2">
              <Avatar className="h-8 w-8">
                <AvatarImage src={user.avatar} alt={user.name} />
                <AvatarFallback className="bg-primary/20 text-primary">
                  {user.name.split(' ').map(n => n[0]).join('').toUpperCase()}
                </AvatarFallback>
              </Avatar>
              <div className="flex flex-col space-y-1">
                <p className="text-sm font-medium text-foreground">{user.name}</p>
                <p className="text-xs text-muted-foreground">{user.email}</p>
              </div>
            </div>
            <DropdownMenuSeparator />
            <DropdownMenuItem data-testid="menu-profile">
              <User className="mr-2 h-4 w-4" />
              Profile
            </DropdownMenuItem>
            <DropdownMenuItem data-testid="menu-billing">
              Billing
            </DropdownMenuItem>
            <DropdownMenuItem data-testid="menu-settings">
              Settings
            </DropdownMenuItem>
            <DropdownMenuSeparator />
            <DropdownMenuItem data-testid="menu-logout" className="text-destructive">
              Log out
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    </motion.header>
  );
}

client/src/components/GlassSidebar.tsx.content:
import { useState } from "react";
import { motion } from "framer-motion";
import { 
  Home, 
  Server, 
  Database, 
  DollarSign, 
  Settings, 
  ChevronLeft, 
  ChevronRight,
  Cloud,
  BarChart3,
  Users,
  Shield
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { cn } from "@/lib/utils";

interface SidebarItem {
  id: string;
  label: string;
  icon: React.ElementType;
  badge?: number;
  isActive?: boolean;
}

interface GlassSidebarProps {
  onItemSelect: (itemId: string) => void;
  activeItem?: string;
}

const sidebarItems: SidebarItem[] = [
  { id: "dashboard", label: "Dashboard", icon: Home },
  { id: "resources", label: "Resources", icon: Server, badge: 12 },
  { id: "databases", label: "Databases", icon: Database, badge: 3 },
  { id: "costs", label: "Cost & Billing", icon: DollarSign },
  { id: "analytics", label: "Analytics", icon: BarChart3 },
  { id: "users", label: "IAM Users", icon: Users, badge: 8 },
  { id: "security", label: "Security", icon: Shield },
  { id: "settings", label: "Settings", icon: Settings },
];

export default function GlassSidebar({ onItemSelect, activeItem = "dashboard" }: GlassSidebarProps) {
  const [isCollapsed, setIsCollapsed] = useState(false);

  return (
    <motion.div
      initial={{ x: -300 }}
      animate={{ x: 0 }}
      transition={{ duration: 0.3 }}
      className={cn(
        "h-full bg-sidebar/80 backdrop-blur-xl border-r border-white/10 transition-all duration-300 relative",
        isCollapsed ? "w-16" : "w-64"
      )}
    >
      {/* Header */}
      <div className="p-4 border-b border-white/10">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-primary/20 rounded-lg flex items-center justify-center">
            <Cloud className="w-5 h-5 text-primary" />
          </div>
          {!isCollapsed && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              className="flex flex-col"
            >
              <span className="font-semibold text-sidebar-foreground text-sm">AWS Manager</span>
              <span className="text-xs text-muted-foreground">Cloud Dashboard</span>
            </motion.div>
          )}
        </div>
      </div>

      {/* Navigation Items */}
      <nav className="p-2 space-y-1">
        {sidebarItems.map((item) => {
          const Icon = item.icon;
          const isActive = activeItem === item.id;

          return (
            <motion.div
              key={item.id}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <Button
                variant="ghost"
                className={cn(
                  "w-full justify-start text-left h-10",
                  isCollapsed ? "px-2" : "px-3",
                  isActive && "bg-sidebar-accent text-sidebar-accent-foreground"
                )}
                onClick={() => onItemSelect(item.id)}
                data-testid={`sidebar-${item.id}`}
              >
                <Icon className={cn("w-4 h-4 flex-shrink-0", isCollapsed ? "" : "mr-3")} />
                {!isCollapsed && (
                  <>
                    <span className="flex-1 text-sm">{item.label}</span>
                    {item.badge && (
                      <span className="ml-auto bg-primary/20 text-primary text-xs px-2 py-0.5 rounded-full">
                        {item.badge}
                      </span>
                    )}
                  </>
                )}
              </Button>
            </motion.div>
          );
        })}
      </nav>

      {/* Collapse Toggle */}
      <div className="absolute bottom-4 left-2 right-2">
        <Button
          variant="ghost"
          size="sm"
          className="w-full"
          onClick={() => setIsCollapsed(!isCollapsed)}
          data-testid="button-toggle-sidebar"
        >
          {isCollapsed ? (
            <ChevronRight className="w-4 h-4" />
          ) : (
            <>
              <ChevronLeft className="w-4 h-4 mr-2" />
              <span className="text-sm">Collapse</span>
            </>
          )}
        </Button>
      </div>
    </motion.div>
  );
}

client/src/components/GreetingBanner.tsx.content:
import { motion, useReducedMotion } from "framer-motion";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Sparkline } from "./Sparkline";
import { Cloud, TrendingUp, AlertCircle, Activity } from "lucide-react";
import { fadeInUp } from "@/utils/motionVariants";

interface GreetingBannerProps {
  userName: string;
  onQuickAction: (action: string) => void;
}

// TODO: remove mock data
const recentActivity = [
  "2 new EC2 instances launched",
  "Cost decreased by 12% this month", 
  "Database backup completed"
];

const quickMetrics = [67, 72, 68, 75, 71, 78, 82];

function getTimeBasedGreeting() {
  const hour = new Date().getHours();
  if (hour < 12) return { text: "Good morning", gradient: "from-amber-400/20 to-orange-500/20" };
  if (hour < 17) return { text: "Good afternoon", gradient: "from-blue-400/20 to-cyan-500/20" };
  return { text: "Good evening", gradient: "from-purple-400/20 to-pink-500/20" };
}

export default function GreetingBanner({ userName, onQuickAction }: GreetingBannerProps) {
  const { text: greeting, gradient } = getTimeBasedGreeting();
  const firstName = userName.split(' ')[0];
  const shouldReduceMotion = useReducedMotion();

  return (
    <motion.div
      variants={shouldReduceMotion ? undefined : fadeInUp}
      initial={shouldReduceMotion ? false : "hidden"}
      animate={shouldReduceMotion ? false : "visible"}
      className="relative overflow-hidden"
    >
      <Card className={`gradient-border noise-overlay bg-gradient-to-r ${gradient} border-white/10`}>
        <div className="p-6">
          <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-6">
            {/* Greeting Section */}
            <div className="space-y-3">
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 bg-primary/20 rounded-xl flex items-center justify-center">
                  <Cloud className="w-6 h-6 text-primary" />
                </div>
                <div>
                  <h1 className="text-2xl font-semibold text-foreground tracking-tight">
                    {greeting}, {firstName}
                  </h1>
                  <p className="text-muted-foreground">
                    Welcome back to your AWS console
                  </p>
                </div>
              </div>

              {/* Recent Activity */}
              <div className="flex flex-wrap items-center gap-2">
                {recentActivity.map((activity, index) => (
                  <motion.div
                    key={index}
                    initial={shouldReduceMotion ? false : { opacity: 0, scale: 0.9 }}
                    animate={shouldReduceMotion ? false : { opacity: 1, scale: 1 }}
                    transition={shouldReduceMotion ? undefined : { delay: 0.2 + index * 0.1 }}
                  >
                    <Badge 
                      variant="secondary" 
                      className="bg-background/50 text-foreground border-white/20 text-xs"
                    >
                      <Activity className="w-3 h-3 mr-1" />
                      {activity}
                    </Badge>
                  </motion.div>
                ))}
              </div>
            </div>

            {/* Quick Stats & Actions */}
            <div className="flex flex-col lg:flex-row items-start lg:items-center gap-6">
              {/* Mini Trend */}
              <div className="flex items-center space-x-3">
                <div className="text-right">
                  <p className="text-sm text-muted-foreground">Resource usage</p>
                  <div className="flex items-center space-x-2">
                    <span className="text-lg font-semibold text-foreground">
                      {quickMetrics[quickMetrics.length - 1]}%
                    </span>
                    <TrendingUp className="w-4 h-4 text-chart-2" />
                  </div>
                </div>
                <Sparkline 
                  data={quickMetrics} 
                  width={60} 
                  height={20}
                  color="hsl(var(--chart-2))"
                />
              </div>

              {/* Quick Actions */}
              <div className="flex space-x-2">
                <Button 
                  size="sm" 
                  variant="outline"
                  className="bg-background/50 border-white/20 hover:bg-background/70"
                  onClick={() => onQuickAction('create-resource')}
                  data-testid="button-quick-create"
                >
                  Create resource
                </Button>
                <Button 
                  size="sm"
                  className="bg-primary hover:bg-primary/90"
                  onClick={() => onQuickAction('view-costs')}
                  data-testid="button-quick-costs"
                >
                  View spending
                </Button>
              </div>
            </div>
          </div>
        </div>
      </Card>
    </motion.div>
  );
}

